    error = "<span class='text-danger'>Wrong mobile number. Please, enter the mobile number again.</span>"
    texto_loading = "Sending data. Please, wait..."
    aviso_privacidad = "<p class='text-danger ico_labelled_text'>You must accept the privacy policy</p>"
    enviado = false; // Flag

    function comprobar_telf(telefono) {
        var pais = 'en';
        switch(pais)
        {
            case 'mx':
                min_length = 9;
                max_length = 11;
                if(telefono.length > min_length && telefono.length < max_length)
                {
                    valido = true;
                }
            break;
            case 'it':
                min_length = 7;
                max_length = 10;
                if(telefono.length > min_length && telefono.length < max_length)
                {
                    valido = true;
                }
            break;
            default:
                var regexp = /^\d{9}$/; 
                var valido = regexp.test(telefono);
            break;
        }

        return valido;
    }

    // Listener
    $("#enviar_telefono_ajax").click( function(event){

        event.preventDefault();

        if(!enviado){

            nombre = $('#nombre').val();
            telefono = $('#telefono').val();
            id_doctor = $('#id_doctor').val();
            id_especialidad = $('#id_especialidad').val();
            id_ciudad = $('#id_ciudad').val();
            id_provincia = $('#id_provincia').val();

            if($('#condiciones_check').prop('checked') == false){
                $('#aviso_condiciones').css('color','#ff0000');
                $('#aviso_condiciones').html("<br>"+aviso_privacidad);
                $('#aviso_condiciones').focus();
            } else {
                $('#aviso_condiciones').html('');
            }

            if(comprobar_telf(telefono)){
                $('#resultado').html('');
            } else {
                $('#telefono').attr('enabled','enabled');
                $('#resultado').css('color','#ff0000');
                $('#resultado').html(error);
                $('#telefono').focus();
            }

            if(comprobar_telf(telefono) && $('#condiciones_check').prop('checked')){
                enviado = true;
                $('#nombre').attr('disabled','disabled');
                $('#telefono').attr('disabled','disabled');
                $('#condiciones_check').attr('disabled','disabled');
                $('#resultado').css('color','#777777');
                $('#resultado').html("<img style='padding-right: 5px;vertical-align: middle;' width='20px' title='Teléfono registrado' alt='Teléfono registrado' src='https://topdoctors.co.uk/img/ajax-loader.gif'/>" + texto_loading);
                $.ajax({
                    type: 'POST',
                    url: 'https://www.topdoctors.co.uk/staticspages/popup_callback_registrar/',
                    data: {
                        'name': nombre,
                        'telephone_number': telefono, 
                        'doctor_id': id_doctor,
                        'specialization_id': id_especialidad,
                        'city_id': id_ciudad,
                        'province_id': id_provincia
                    },
                    success: function(resultado){
                        $('#resultado').css('color','#006B90');
                        $('#resultado').html(resultado);
                    }
                });
            }
        }
    });,London, England, United Kingdom,Dr Gaurav Atreja

