    enviado = false; // Flag

    function comprobar_telf(telefono) {
        var pais = 'en';
        switch(pais)
        {
            case 'mx':
                min_length = 9;
                max_length = 11;
                if(telefono.length > min_length && telefono.length < max_length)
                {
                    valido = true;
                }
            break;
            case 'it':
                min_length = 7;
                max_length = 10;
                if(telefono.length > min_length && telefono.length < max_length)
                {
                    valido = true;
                }
            break;
            default:
                var regexp = /^\d{9}$/; 
                var valido = regexp.test(telefono);
            break;
        }

        return valido;
    }

